mapper.select:
SELECT worker.name,COUNT(mistake.id) countMis,COUNT(achievement.id) countAch FROM mistake,employ,worker,
achievement,dossier WHERE employ.Worker_id=#{workerId1} and worker.id=#{workerId1} and dossier.Employ_id=employ.id and
mistake.Id=dossier.Mistake_id and achievement.id = dossier.Achievement_id;

SELECT worker.name,COUNT(mistake.id) countMis,COUNT(achievement.id) countAch FROM mistake,employ,worker,
achievement,dossier WHERE employ.Worker_id=#{workerId2} and worker.id=#{workerId2} and dossier.Employ_id=employ.id and
mistake.Id=dossier.Mistake_id and achievement.id = dossier.Achievement_id;
